{
  "openapi": "3.0.0",
  "info": {
    "title": "Netlify",
    "description": "Deploy static websites",
    "version": "1.0"
  },
  "servers": [
    {
      "url": "https://app.netlify.com"
    }
  ],
  "paths": {
    "/partner-drop/api/v1/drop": {
      "post": {
        "operationId": "deployToNetlify",
        "x-openai-isConsequential": false,
        "summary": "Deploy files to Netlify to create website. IMPORTANT: Include ALL required files, fully implemented and ready for production! Old files must be re-uploaded.",
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "files": {
                    "description": "file paths &amp; file content to deploy",
                    "type": "array",
                    "items": {
                      "type": "object",
                      "properties": {
                        "path": {
                          "description": "file path",
                          "type": "string"
                        },
                        "content": {
                          "description": "file content",
                          "type": "string"
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Deploy"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Deploy": {
        "type": "object",
        "properties": {
          "url": {
            "type": "string",
            "description": "site URL"
          },
          "claim_url": {
            "type": "string",
            "description": "Save your site via the Claim URL! Or it will be deleted in one hour!!! The URL must contain the token inside the hash for it to work. After claiming, users can use netlify dashboard to download files, setup a custom domain, and configure settings."
          },
          "important_message": {
            "type": "string",
            "description": "status"
          }
        }
      }
    }
  }
}

