{
  "openapi": "3.0.1",
  "info": {
    "title": "Prompt Perfect",
    "description": "A plugin that rephrases prompts deemed unclear, overly brief, or missing necessary information into clearer, more specific, and contextual prompts.",
    "version": "v1"
  },
  "paths": {
    "/rephrase": {
      "post": {
        "operationId": "rephrasePrompt",
        "summary": "Rephrase the given prompt",
        "x-openai-isConsequential": false,
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Prompt"
              },
              "example": {
                "text": "Write a tweet"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/RephrasedPrompt"
                },
                "example": {
                  "text": "Compose a highly detailed and engaging tweet. Keep the tweet within the 280-character limit"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Prompt": {
        "type": "object",
        "properties": {
          "conversation_id": {
            "type": "string",
            "description": "The conversation ID."
          },
          "text": {
            "type": "string",
            "description": "The prompt text to be rephrased."
          }
        }
      },
      "RephrasedPrompt": {
        "type": "object",
        "properties": {
          "conversation_id": {
            "type": "string",
            "description": "The conversation ID."
          },
          "rephrased": {
            "type": "object",
            "properties": {
              "text": {
                "type": "string",
                "description": "The rephrased prompt text."
              }
            }
          }
        }
      }
    }
  },
  "servers": [
    {
      "url": "https://plugin.promptperfect.xyz"
    }
  ]
}

{
  "openapi": "3.1.0",
  "info": {
    "title": "TARS YT summarizer",
    "description": "Summarize long YT videos",
    "version": "v1.0.0"
  },
  "servers": [
    {
      "url": "https://youtubetranscript.com"
    }
  ],
  "paths": {
    "/": {
      "get": {
        "operationId": "getSummarizedTranscript",
        "summary": "Get summarized transcript of a YouTube video",
        "parameters": [
          {
            "name": "server_vid2",
            "in": "query",
            "description": "YouTube video ID",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful response",
            "content": {
              "application/xml": {
                "example": "&lt;transcript&gt;Summarized transcript goes here...&lt;/transcript&gt;"
              }
            }
          },
          "400": {
            "description": "Bad request",
            "content": {
              "application/json": {
                "example": {
                  "error": "Invalid video ID"
                }
              }
            }
          },
          "404": {
            "description": "Video not found",
            "content": {
              "application/json": {
                "example": {
                  "error": "Video not found"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {}
  }
}
